import React from 'react';
import Header from '../components/Header.jsx'
import './forms-style.css'
import nyanDuck from '../assets/Nyan_kaczka.png'
import Ships from '../assets/island-statki2.png';

class Profile extends React.Component {
    constructor() {
        super();
        this.state = {
            name: '',
            password:''
        };

        this.handleChange = this.handleChange.bind(this);
        this.handlePassword = this.handlePassword.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);

    }
    handleChange(event) {
        this.setState({name: event.target.value});

    }
    handlePassword(event) {
        this.setState({password: event.target.value});

    }

    handleSubmit(event) {

       console.log('tutaj wolanie na backend')

    }

    render() {

        return (
            <div className="app">
                <Header />
                <h2 className="form-title">Twój profil</h2>

                <div className="form-container">
                <img src={Ships} alt="Statki" className="gameStatsImage" />

                    <form onSubmit={this.handleSubmit} action="/">
                      <input type="text" value={this.state.name} onChange={this.handleChange} placeholder="login/email" defaultValue={this.state.mail} /><br/>
                       <input type="password" value={this.state.password} onChange={this.handlePassword} placeholder="hasło" defaultValue={this.state.password}/><br/>

                    <input type="submit" value="Ustawienia" />
                  </form>
                </div>
                <img src={nyanDuck} alt="nyan kaczka rotated" className="nyanRotated" />

            </div>
        )
    }
}


export default Profile;
